def construct_fence(p):
    # return 1e7
    return "H"*int((1e6)/int(p[1:].replace(",","")))


print(construct_fence("$50,000"), "HHHHHHHHHHHHHHHHHHHH")
print(construct_fence("$100,000"),"HHHHHHHHHH")
print(construct_fence("$1,000,000"), "H")
print(construct_fence("$500,000"), "HH")
print(construct_fence("$20,000"), "HHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHH")
print(construct_fence("$10,000"), "HHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHH")
print(construct_fence("$5000"), "HHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHHH")
print(construct_fence("$1000"), "")
